import React, { Component } from 'react'



export class LandlordShow extends Component {
    state = {
        landlord: {},
        loading: true
    }

    componentDidMount() {
        const landlord = this.props.landlords.find(landlord => landlord.id === parseInt(this.props.match.params.id, 10))
        if (landlord) {
            this.setState({
                landlord,
                loading: false
            })
        }
    }

    render() {
        // if (this.state.loading) {
        //     return <p>Loading....</p>
        // }
        return (
            <div>
                <p>Landlord Name is {this.state.landlord.name}</p>
                <p>Landlord Age is {this.state.landlord.age}</p>
            </div>
        )
    }
}


export default LandlordShow
