const initialState = {
    loading: true,
    landlords: []
}

export default (state = initialState, action) => {
    switch (action.type) {
        case "LOADING":
            return { ...state, loading: true }

        case "LOAD_LANDLORDS":
            return { ...state, loading: false, landlords: action.landlords }

        case "ADD_LANDLORD":
            return { ...state, landlord: action.landlord }

        case "DELETE_LANDLORD":
            let newLandlords = state.filter(landlord = landlord.id !== action.payload)
            return [...newLandlords]

        default:
            return state;

    }
}
