import React, { Component } from 'react'


export class LandlordShow extends Component {

    constructor(props) {
        super(props);

        let paramsId = parseInt(this.props.match.params.id, 10)

        let landlord = props.landlords.find(landlord => landlord.id === paramsId)

        this.state = {
            landlord: landlord ? landlord : { name: "N/A", age: "N/A" }, state: "N/A", city: "N/A", number_of_houses: "N/A"
        }
    }

    render() {
        // const { name, age, state, city, number_of_houses } = this.state.landlord;

        return (


            < div >
                <h3>{this.props.name}</h3>
                <p>Age: {this.props.age}</p>


            </div >
        )
    }
}

export default LandlordShow

